callback_with_context.context_function_id:
( ability_holder_component = Component("08795FD3-D435-4911-9245BB2E79B38EA8"),
clicked_slot_initiative_set_key = self.GetProperty("slot_initiative_set_key"),
slot_component = ability_holder_component.ChildList.Filter(IsVisible && GetProperty("kairos_initiative_set_key") == clicked_slot_initiative_set_key).FirstContext ) =>
{
 Do( slot_component.ChildContext("dlc26_fragments_abilities_selector").SetVisible(false), self.SetVisible(false) )
}

#1. propagate this slot's initiative/spell/effect/ability if one is active#
( active_initiative_list = InitiativeList.Filter(IsActive) ) =>
{
 GetIf( active_initiative_list.IsEmpty == false, active_initiative_list.FirstContext )
}

#0. setting tooltip layout using "layout_path" component property that will be used by ContextLayoutTooltip#
( is_slot_occupied = ActiveInitiatives.IsEmpty == false ) =>
{
 Do(self.SetProperty("layout_path", GetIfElse( is_slot_occupied, "ui/common ui/special_ability_tooltip", "ui/campaign ui/dlc26_fragments_sorcery_locked_ability_tooltip" )), 
self.SetProperty("alternate_parent_dock_point", GetIfElse( is_slot_occupied, "none", "cre")))
}

#open spell browser when selecting spell#
( ability_context = self.StoredContext("CcoUnitAbility") ) =>
{
 DoIf( self.CurrentState != "inactive" && self.CurrentState != "inactive_hover" && IsContextValid(ability_context), ability_context.OpenSpellBrowser )
}

