( right_head_initiative_list = InitiativeList.Filter( StringContains(InitiativeContext.Key, "death") || StringContains(InitiativeContext.Key, "beast") || StringContains(InitiativeContext.Key, "shadow") || StringContains(InitiativeContext.Key, "fire") ) ) =>
{
 InitiativeList.Filter( StringContains(InitiativeContext.Key, "life") || StringContains(InitiativeContext.Key, "metal") || StringContains(InitiativeContext.Key, "light") || StringContains(InitiativeContext.Key, "heaven") ).Append(right_head_initiative_list)
}

