DoIfElse( selected && visible == false, self.SetVisible(true), DoIfElse( selected == false && visible, self.TriggerAnimation("hide"), DoIf(self.CurrentAnimation == "hide", self.TriggerAnimation("show"))))
}

(target = self.Position + self.Dimensions / 2.0 + self.GetAnimationFrameProperty("particle_move", 2, "position"),
 speed = TrueRandomInRange(80, 110) / 100.0) => {

  self.AnimationState.SetAnimation(GetIfElse(TrueRandomInRange(0, 100) > 50, "crawl_01", "crawl_02"), 0, true);
  self.AnimationState.Track(0).SetAnimationTimeScale(466 / self.GetAnimationFrameProperty("particle_move", 1, "interpolation_time").x);
  self.Skeleton.RootBone.SetRotation(RadiansToDegrees(-ArcTan2(target.y - self.Position.y - self.Dimensions.y / 2.0, target.x - self.Position.x - self.Dimensions.x / 2.0)));
  self.AnimationState.UpdateAnimation(0);
  self.AnimationState.Apply(self.Skeleton);
  self.ChildContext("lifetime").TriggerAnimation("start");
  self.SetInteractive(true)
}

