callback_with_context.context_function_id:
( ability_holder_component = Component("08795FD3-D435-4911-9245BB2E79B38EA8"),
this_initiative_set_key = InitiativeSetContext.Key,
tooltip_host_component = ability_holder_component.ChildList.Filter(IsVisible && GetProperty("kairos_initiative_set_key") == this_initiative_set_key).FirstContext ) =>
{
	SelectedCharacter.Rank < RoundFloat(ToNumber(tooltip_host_component.GetProperty("unlocked_at_rank")))
}

( ability_holder_component = Component("08795FD3-D435-4911-9245BB2E79B38EA8"),
this_initiative_set_key = InitiativeSetContext.Key,
tooltip_host_component = ability_holder_component.ChildList.Filter(IsVisible && GetProperty("kairos_initiative_set_key") == this_initiative_set_key).FirstContext ) =>
{
 Format( Loc("wh3_kairos_fragments_of_sorcery_unlock_slot_at_level"), RoundFloat(ToNumber(tooltip_host_component.GetProperty("unlocked_at_rank"))) )
}
