callback_with_context.context_function_id:
( initiatives_holder_high_res_component = Component("9486A26F-6525-4389-A41B449876AC10A9"),
initiative_context_list = initiatives_holder_high_res_component.ChildList.Filter((IsSelected || CurrentState == "for_deactivation" || CurrentState == "for_deactivation_down" || CurrentState == "for_deactivation_down_off" || CurrentState == "for_deactivation_hover") && IsVisible).Transform( (comp) => comp.StoredContext("CcoCampaignInitiative") ) ) =>
{
 DoIf( self.CurrentState != "inactive" && self.CurrentState != "inactive_hover",
  initiative_context_list.ForEach(Toggle(set.GetFirstAvailableSlot)) )
}

( initiatives_holder_low_res_component = Component("238FCB99-81E5-4F9F-924154F798E15950"),
initiative_context_list = initiatives_holder_low_res_component.ChildList.Filter((IsSelected || CurrentState == "for_deactivation" || CurrentState == "for_deactivation_down" || CurrentState == "for_deactivation_down_off" || CurrentState == "for_deactivation_hover") && IsVisible).Transform( (comp) => comp.StoredContext("CcoCampaignInitiative") ) ) =>
{
 DoIf( self.CurrentState != "inactive" && self.CurrentState != "inactive_hover",
  initiative_context_list.ForEach(Toggle(set.GetFirstAvailableSlot)) )
}

( initiatives_holder_high_res_component = Component("9486A26F-6525-4389-A41B449876AC10A9"),
initiative_context_list = initiatives_holder_high_res_component.ChildList.Filter(IsSelected && IsVisible).Transform( (comp) => comp.StoredContext("CcoCampaignInitiative") ) ) =>
{
 GetIfElse( initiative_context_list.IsEmpty == false, Floor(initiative_context_list.Sum(CostContext.PooledResourceTransactionList.FirstContext(Value < 0).AbsoluteValue)), "-" )
}

