callback_with_context.context_function_id:
DoIf(self.CurrentState != "inactive",
  DoIfElse(self.CurrentState != "selected",
    self.SetState("selected"),
 self.SetState("hover")
  ),
  # If not Greenskin, deselect the other upgrades. #
  DoIf(PlayersFaction.CultureContext.IsGreenskins == false,
    self.ParentContext.ChildList.ForEach(
   (c) => { DoIf(c.CurrentState == "selected" && c != self, c.SetState("active")) }
 )
  ),
  UiMsg("ScrapUnitPurchasableEffectSelectionChanged")
)

( selected_comps_total_inherent_cost = Component("list_box", "scrap_upgrades_parent").ChildList.Filter(CurrentState == "selected").Transform( (comp) => comp.StoredContext("CcoUnitPurchasableEffectRecord") ).Sum(Abs(CostContext.TreasuryCost)),
num_upgrades = Component("list_box", "scrap_upgrades_parent").ChildList.Count(CurrentState == "selected"),
num_selected_units = Component("units", "main_units_panel").UpgradingUnitsContextList.Size ) => 
{
 (selected_comps_total_inherent_cost + num_upgrades) * num_selected_units
}
