callback_with_context.context_function_id:
(query = Component("8859C627-B500-4992-AE5798D64B81ADB4").CurrentText) => {
  self.SetVisible(
    query.IsEmpty ||
    UnitContext.Name.Contains(query) ||
 UnitDetailsContext.AbilityDetailsList.Any(Name.Contains(query) || (AbilityContext && AbilityContext.IsHiddenInUI == false && AbilityContext.Name.Contains(query)))
  )
}
