callback_with_context.context_function_id:
(Upgrades = AvailableUpgradeList.Filter(IsContextValid(AncillaryContext) == false && AbilityContext.ManaUsed > 0), AnyNotEquipped = Upgrades.Any(IsEquipped == false)) => {
  Upgrades.ForEach(DoIf(IsEquipped != AnyNotEquipped, ToggleEquipped))
}

(Upgrades = AvailableUpgradeList.Filter(IsContextValid(AbilityContext.AbilityGroupContext) && AbilityContext.AbilityGroupContext.IsRunicLore), AnyNotEquipped = Upgrades.Any(IsEquipped == false)) => {
  Upgrades.ForEach(DoIf(IsEquipped != AnyNotEquipped, ToggleEquipped))
}

(Upgrades = AvailableUpgradeList.Filter(IsContextValid(AncillaryContext) == false && AbilityContext.ManaUsed == 0 && (IsContextValid(AbilityContext.AbilityGroupContext) == false || AbilityContext.AbilityGroupContext.IsRunicLore == false)), AnyNotEquipped = Upgrades.Any(IsEquipped == false)) => {
  Upgrades.ForEach(DoIf(IsEquipped != AnyNotEquipped, ToggleEquipped))
}

(Upgrades = AvailableUpgradeList.Filter(IsContextValid(AncillaryContext) && AncillaryContext.CategoryContext.Key != "form"), AnyNotEquipped = Upgrades.Any(IsEquipped == false)) => {
  Upgrades.ForEach(DoIf(IsEquipped != AnyNotEquipped, ToggleEquipped))
}

